document.addEventListener('DOMContentLoaded', () => {
    var time,seconds, minutes, inter, exerciseCounter, stopCounter;

    var conf = {
        timePerExercise: 10,
        timePerBreak: 10,
        beepsQuantity: 3
    }

    var exercise = false;
    var timerStartBtn = document.querySelector("#timer_counter");
    var mask = document.querySelector("#mask");
    var btnModal = document.querySelector("#btnShowModal");
    var form = document.querySelector("#form_config");
    var btnCloseModal = document.querySelector("#btnCloseModal");
    var btnSaveConf = document.querySelector("#btn_save");
    var beepsQuantitySelect = document.querySelector("#beeps_quantity");

    var timePerExerciseInput = document.querySelector("#timePerExercise");
    var timePerBreakInput = document.querySelector("#timePerBreak");


    timePerExerciseInput.addEventListener('focus', (e) => e.target.select())
    timePerBreakInput.addEventListener('focus', (e) => e.target.select())
    
    timerStartBtn.addEventListener("click", startTimer);
    mask.addEventListener('click', closeConfModal);
    btnModal.addEventListener('click', showConfModal);
    btnCloseModal.addEventListener('click', closeConfModal);
    btnSaveConf.addEventListener("click", saveConf);

    function startTimer() {
        resetTimer();
        inter = setInterval(() => {
            seconds++;
            document.querySelector("#timer_counter").innerHTML = ++time;
            document.querySelector("#timer_counter_sec").innerHTML = getSecsFormat(seconds);

            if(exercise && time >= conf.timePerExercise) {
                exercise = false;
                time = 0;
                document.querySelector("#exercise").innerHTML = "Ejercicios: " + ++exerciseCounter;
                document.querySelector("#exercise").classList.remove('selected');
                document.querySelector("#stop").classList.add('selected');
                beep();
                
            } else if(!exercise && time >= conf.timePerBreak) {
                exercise = true;
                time = 0;
                document.querySelector("#stop").innerHTML = "Descansos: " + ++stopCounter;
                document.querySelector("#stop").classList.remove('selected');
                document.querySelector("#exercise").classList.add('selected');
                beep();
            }
        }, 1000);
    }

    function resetTimer() {
        time = 0;
        exerciseCounter = 0;
        stopCounter = 0;
        seconds = 0;
        minutes = 0;
        exercise = true;
        clearInterval(inter);
        document.querySelector("#stop").innerHTML = "Descansos: " + stopCounter;
        document.querySelector("#exercise").innerHTML = "Ejercicios: " + exerciseCounter;
        document.querySelector("#stop").classList.remove('selected');
        document.querySelector("#exercise").classList.add('selected');
    }

    function saveConf() {
        conf.timePerExercise = timePerExerciseInput.value;
        conf.timePerBreak = timePerBreakInput.value;
        conf.beepsQuantity = beepsQuantitySelect.value;
        localStorage.setItem('conf_fitness', JSON.stringify(conf));
    }

    function getConfFromLocal() {
        if (localStorage.getItem('conf_fitness')) {
            conf = JSON.parse(localStorage.getItem('conf_fitness'))
            console.log("loaded data", conf)
        }
    }

///**Comentario de prueba para integrar con rama prod en remoto */

    function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        let delay = 400;

        for (let i = 0; i < conf.beepsQuantity; i++) {
            if (i === 0) snd.play();
            else {
                setTimeout(() => {
                   snd.play(); 
                }, i*delay);
            }
        }

    }

    function getSecsFormat(secs) {
        var sec_num = parseInt(secs);
        var hours   = Math.floor(sec_num / 3600);
        var minutes = Math.floor((sec_num - (hours * 3600)) / 60);
        var seconds = sec_num - (hours * 3600) - (minutes * 60);
    
        if (hours   < 10) {hours   = "0"+hours;}
        if (minutes < 10) {minutes = "0"+minutes;}
        if (seconds < 10) {seconds = "0"+seconds;}
        return hours+':'+minutes+':'+seconds;
    }

    function showConfModal() {
        mask.classList.add('show-mask');
        form.classList.add('show-modal-conf')
    }
    
    function closeConfModal() {
        mask.classList.remove('show-mask');
        form.classList.remove('show-modal-conf')
    }

    getConfFromLocal();
});